mapper.insert:
insert into user (user_id, username, password, 
  phonenum, type, image, license_code , license_url, location
  )
values (#{userId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{phonenum,jdbcType=VARCHAR}, #{type,jdbcType=TINYINT}, #{image,jdbcType=VARCHAR},#{licenseCode,jdbcType=VARCHAR},
  #{licenseUrl,jdbcType=VARCHAR}, #{location,jdbcType=VARCHAR}
  )

mapper.select:
select

from user
where phonenum = #{phonenum}
and password = #{password}

mapper.update:
update user
set password = #{password}
where phonenum = #{phonenum}
